{
    "TESTRUN": result.simulacao = true ? "X" : "",
    "SALES_HEADER_IN": {
                        "SALES_ORG": $substringBefore(result.orgVendas, "-"),
                        "DLVSCHDUSE": $substringBefore(result.utilizacao, " -"),
                        "DISTR_CHAN":  $substringBefore(result.canalDistribuicao, "-"),
                        "DIVISION":  $substringBefore(result.setorAtividade, "-"),
                        "SALES_GRP": $substringBefore(result.codVendedor, "-"),
                        "SALES_OFF": $substringBefore(result.escritorioVendas, "-"),
                        "PMNTTRMS": $substringBefore(result.codCondPagamento, "-"),
                        "PURCH_NO_C": result.codSimulacao,
                        "BILL_DATE": result.dataFaturamento != null ? $toMillis(result.dataFaturamento) ~> $fromMillis("[Y0001].[M01].[D01]") : null,
                        "PO_DAT_S": result.dataEntrega != null ? $toMillis(result.dataEntrega) ~> $fromMillis("[Y0001].[M01].[D01]") : null,
                        "INCOTERMS1": $substringBefore(result.incoterms, "-"),
                        "S_PROC_IND": result.tipoCarga = "Fracionado" ? "FRAC" : "LOT",
                        "SHIP_TYPE": result.tipoExpedicao,
                        "PURCH_NO_S": result.numPedidoCliente,
                        "PRICE_GRP": result.mostruario = true ? "99": ""
                       },
    "SALES_PARTNERS": [{
                        "PARTN_NUMB": result.codCliente,
                        "PARTN_ROLE": "AG"
                      },
                      {
                        "PARTN_NUMB": result.codClienteCobranca,
                        "PARTN_ROLE": "RG"
                      },
                      {
                        "PARTN_NUMB": result.codClienteEntrega,
                        "PARTN_ROLE": "WE"
                      },
                      {
                        "PARTN_NUMB": result.transportadora != null ? $join([$substring("0000000000",$length(result.transportadora)), result.transportadora]) : "",
                        "PARTN_ROLE": "SP"
                      }],
    "SALES_ITEMS_IN": [$map(result.produtos, function($v){
                        {
                            "ITM_NUMBER": $string($v.item),
                            "MATERIAL": $v.codProdutoSap,
                            "PLANT": $substringBefore($$.result.centro, "-"),
                            "SHIP_POINT":  $substringBefore(result.localExp, "-"),
                            "STORE_LOC":  $substringBefore(result.deposito, "-"),
                            "TARGET_QTY": $string($v.qtde),
                            "PRICE_GRP": $$.result.mostruario = true ? "99": ""
                        }
                    })],
    "SALES_CONDITIONS_IN": $append($map(result.produtos, function($v){
                                {
                                    "ITM_NUMBER": $string($v.item),
                                    "COND_TYPE": "ZNEG",
                                    "COND_VALUE": $string($v.valorProposto),
                                    "CURRENCY": "BRL"
                                }
                           }),$map(result.produtos, function($v){
                                {
                                    "ITM_NUMBER": $string($v.item),
                                    "COND_TYPE": "ZDSR",
                                    "COND_VALUE": $string($v.mostruarioPercent*10),
                                    "CURRENCY": "BRL"
                                }
                           })),
    "SALES_TEXT": $append($append([$map(result.produtos, function($v){
                $exists($$.result.justificativa) = true ? {
                    "DOC_NUMBER": " ",
                    "ITM_NUMBER": $string($v.item),
                    "TEXT_ID": "Z001",
                    "LANGU": "P",
                    "LANGU_ISO": " ",
                    "FORMAT_COL": "*",
                    "TEXT_LINE": $$.result.justificativa != null ? $replace($trim($$.result.justificativa), "–", "-") : $$.result.justificativa
                }
                })], 
                [
                $exists($$.result.obsPedido) = true ? {
                    "DOC_NUMBER": " ",
                    "ITM_NUMBER": "000000",
                    "TEXT_ID": "0003",
                    "LANGU": "P",
                    "LANGU_ISO": " ",
                    "FORMAT_COL": "*",
                    "TEXT_LINE": $$.result.obsPedido != null ? $trim($$.result.obsPedido) : $$.result.obsPedido
                }
                ]),
                [
                $exists($$.result.obsNf) = true ? {
                    "DOC_NUMBER": " ",
                    "ITM_NUMBER": "000000",
                    "TEXT_ID": "NFH",
                    "LANGU": "P",
                    "LANGU_ISO": " ",
                    "FORMAT_COL": "*",
                    "TEXT_LINE": $$.result.obsNf != null ? $trim($$.result.obsNf) : $$.result.obsNf
                }
                ]
                )
}

{
"mensagens": $append(result.RETURN[NUMBER != "233"].MESSAGE, $count(result.RETURN[NUMBER = "999"][$contains(MESSAGE, "Justificativa de preço")]) > 0 ? "Valor negociado fora da margem de negociação") 
}
